import React, { Component } from 'react'
import ReactDOM from 'react-dom'

class CurencyChange extends React.Component{
    constructor(props){
        super(props);

    }
    changeCurency(value,scale){
        switch(scale){
            case "Pln":
                this.setState({Pln: value * 4.5, Eur: value});
                return;
                case "Eur":
                    this.setState({Eur: value * 0.45, Pln: value});
                return;
        }
    }
    render(){
        return<>
        <p>Pln</p>
        <input type="text" value={this.state.Pln} onChange={(event) => this.changeCurency(event.target.value) }/>
        <p>Eur</p>
        <input type="text" value={this.state.Eur} onChange={(event) => this.changeCurency(event.target.value) }/>
        </>
    }
}
export default CurencyChange;
