import React, { Component } from 'react'
import ReactDOM from 'react-dom'

class Time extends React.Component {

    constructor(props){
        super(props);
        this.state = {date: new Date()}
    }

    componentDidMount(){
        setInterval(() => this.tick(), 1000);
        }
    tick() {
            this.setState({
              date: new Date()
            });
    }
    render() {
        return (
     <div>
                <h1>Hello, world!</h1>
                <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
     </div>
        );
    }
}

export default Time;
